glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in lowp vec4 a_color;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
out lowp vec4 v_color;
void main () {
  mat4 mvp;
  mvp = cc_matViewProj;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
uniform sampler2D texture2;
in mediump vec2 v_uv0;
uniform Time {
  float time;
};
in lowp vec4 v_color;
void main () {
  vec4 c = v_color * texture2D(texture, v_uv0) * texture2D(texture2, v_uv0);
  gl_FragColor = c;
  float temp = v_uv0.x - time;
  if (temp <= 0.0) {
      float temp2 = abs(temp);
      if (temp2 <= 0.2) {
          gl_FragColor.a *= 1.0 - temp2/0.2;
      } else {
          gl_FragColor.a = 0.0;
      }
  }
}

